mapper.select:
SELECT Count(*)
FROM USER
WHERE user_id = #{userId}

SELECT *
FROM USER
WHERE user_id = #{userId}
and user_password = #{userPassword}
LIMIT 1

SELECT USER_PASSWORD
FROM USER
WHERE user_id = #{userId}
AND user_phone = #{userPhone}

SELECT user_id
FROM USER
WHERE user_name = #{userName}
AND user_phone = #{userPhone}
AND user_birthdate = #{userBirthdate}

mapper.update:
UPDATE USER
SET total_score = #{totalScore}
WHERE user_id = #{userId}
